import { Component, OnInit } from '@angular/core';
import { Location } from '@angular/common';
import { ActivatedRoute } from '@angular/router';
// Import Services
import { CopyrightRegistrationService } from '../../services/copyright-registration.service';
import { CopyrightSharesService } from '../../services/copyright-shares.service';
// Import Models
import { CopyrightRegistration } from '../../domain/ccim_db/copyright-registration';
import { CopyrightShares } from '../../domain/ccim_db/copyright-shares';

