/**
 * This component allows to edit a CopyrightRegistration
 */
@Component({
    selector: 'app-copyright-registration-edit',
    templateUrl: 'copyright-registration-edit.component.html',
    styleUrls: ['copyright-registration-edit.component.css']
})
export class CopyrightRegistrationEditComponent implements OnInit {
    item: CopyrightRegistration;
    listCopyrightSharesCopyrightshares: CopyrightShares[];
    model: CopyrightRegistration;
    formValid: Boolean;

    constructor(
    private copyrightregistrationService: CopyrightRegistrationService,
    private copyrightsharesService: CopyrightSharesService,
    private route: ActivatedRoute,
    private location: Location) {
        // Init item
        this.item = new CopyrightRegistration();
    }

    /**
     * Init
     */
    ngOnInit() {
        this.route.params.subscribe(param => {
            const id: string = param['id'];
            if (id !== 'new') {
                this.copyrightregistrationService.get(id).subscribe(item => this.item = item);
            }
            // Get relations
            this.copyrightsharesService.list().subscribe(list => this.listCopyrightSharesCopyrightshares = list);
        });
    }


    /**
     * Save CopyrightRegistration
     *
     * @param {boolean} formValid Form validity check
     * @param CopyrightRegistration item CopyrightRegistration to save
     */
    save(formValid: boolean, item: CopyrightRegistration): void {
        this.formValid = formValid;
        if (formValid) {
            if (item._id) {
                this.copyrightregistrationService.update(item).subscribe(data => this.goBack());
            } else {
                this.copyrightregistrationService.create(item).subscribe(data => this.goBack());
            } 
        }
    }

    /**
     * Go Back
     */
    goBack(): void {
        this.location.back();
    }


}



